import React, { Component,useState, Fragment } from 'react'
import {BrowserRouter as Router,Route,Switch} from 'react-router-dom';
import axios from "axios";
import {Redirect,Link} from 'react-router-dom'
// import HomeNav from './HomeNav'
// import logo from '../assets/logo.png'
class AddProfile extends Component {
  constructor(props) {
    super(props);

    this.state={
      name:"",
      slug:"",
      govtid:"",
     phone:"",
      email:"",
      address:"",
      isAuth: false,
    };
    this.onChange = this.onChange.bind(this);
  }
  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
 }
 onSubmit = async (e) => {
  e.preventDefault();

  const register={
    name:this.state.name,
    slug:this.state.slug,
    govtid:this.state.govtid,
    phone:this.state.phone,
    email:this.state.email,
    address:this.state.address
  };
  const body = JSON.stringify(register);
  const token = sessionStorage.getItem("token");
  const config = {
    headers: {
      Authorization: `Bearer ${token}`,
      "Content-Type": "application/json",
    },
  }
  console.log(token);
  try {
    const res = await axios.post(
                                 `http://localhost:5000/api/v1/company`,
                                  body,
                                  config
                                 );
   sessionStorage.setItem("isAuth", true);
   this.setState({
                  isAuth: true,
                });
}catch (error) {
                alert("Sorry Something Wrong!!");
               }
 }
  render() {
    return (
      <Fragment>
      { this.state.isAuth ?
       ( <Redirect isAuth={this.state.isAuth} to="/company/c_dash" />
       ):
       (
      <div>
                    
        <form method="post" onSubmit={this.onSubmit}>
          <div className="container itmtop">
          <div class="page-wrapper p-t-50 p-b-50">
              <div class="wrapper wrapper--w900 ">
                <div class="card cardH card-6 ">
			<div className="mx-auto"> 
              {/* <img src={logo} style={{width:'180px'}}></img> <hr/> */}
              <div class="card-heading m-4">
                    <h2 class="title text-dark">Add Profile</h2>
                  </div>
               
                  <div class="card-body">
                  <div class="form-row frow">
                  <div class="name">Scheme Title:</div>
                 <div class="value">
                <input 
                type="text" 
                class="input--style-6"
                name="name"
                value={this.state.name}
                onChange={this.onChange} 
                required/>
                </div>
                </div>

                      
                  <div class="form-row frow">
                  <div class="name">Scheme Title:</div>
                 <div class="value">
                 <input 
                 type="text" 
                 className="input--style-6" 
                 placeholder="govtid" 
                 name="govtid" 
                 value={this.state.govtid}
                 onChange={this.onChange}
                 required />
                 </div>
                 </div>

                   <div class="form-row frow">
                  <div class="name">Scheme Title:</div>
                 <div class="value">
                 <input 
                 type="text" 
                 className="input--style-6" 
                 placeholder="Add slug"
                 name="slug" 
                value={this.state.slug}
                onChange={this.onChange} 
                 required />
                </div>
                </div>
                

                
                  <div class="form-row frow">
                  <div class="name">Scheme Title:</div>
                 <div class="value">
                  <input 
                  type="text" 
                  className="input--style-6" 
                  name="email"
                  value={this.state.email}
                  onChange={this.onChange} 
                  required/>
                  </div>
                  </div>
                  

                  <div class="card-body">
                  <div class="form-row frow">
                  <div class="name">Scheme Title:</div>
                 <div class="value">
                  <input 
                  type="text" 
                  className="col-md-5 ml-4" 
                  placeholder="contact number"
                  name="phone" 
                  value={this.state.phone}
                  onChange={this.onChange} 
                  required/>
                  </div>
                  </div>
                  </div>
                
                  <div class="card-body">
                  <div class="form-row frow">
                  <div class="name">Scheme Title:</div>
                 <div class="value">                         
               <textarea 
               placeholder="Address"
               name="address" 
               value={this.state.address}
               onChange={this.onChange}>
               </textarea>
               </div>
               </div>
               </div>


                <div className="row" style={{marginLeft:'22%'}}>
                 <input type="submit" className="btn btn-blue" value="Register" name="submit"></input>
                  <input type="reset" className="btn btn-dark col-md-3 ml-5" value="Reset"></input>
                </div>
                {/* <p className="mx-auto"> Already have an account?<Link to="/login"> Sign In</Link></p> */}
					  </div>
                      </div>
          </div>
          </div>
          </div>
          </div>
          
        </form>
      </div>
      )};
      </Fragment>
    )
  }

}
export default AddProfile
