import React, { Component } from "react";
import "./CSS/App.css";

class Login extends Component {
  state = {
    email: "",
    password: "",
    type: "",
  };
  componentDidMount() {
    this.setState(() => ({
      type: this.props.match.params,
    }));
  }
  onChange(e) {
    this.setState({ [e.target.name]:e.target.value });
  
    // console.log("123");
  }
  onSubmit  =async (e) => {
    e.preventDefault();
     const login ={
       email:this.state.email,
       password:this.state.password,
     };

     const body=JSON.stringify(login);
     const config={
       headers:{
         "Content-Type":"application/json",
       },
     };

    if (this.state.username === "") {
      // this.props.setAlert("Please Enter Something", "light");
      console.log(this.state.username);
    } else {
      console.log(this.state.username);

      // this.props.searchUser(this.state.text);
      // this.setState({ text: "" });
    }

    
  };
  render() {
    // console.log(this.props.match.params);
    // const { type } = this.props.match.params;
    const username = this.state.username;
    const password = this.state.password;
    const { type } = this.state.type;
    let social = {};
    let signup, login;
    if (type === "user") {
      social = "#12055c";
      signup = <a href={`/signup`}>Sign Up</a>;
    } else {
      social = "#d48b04";
      signup = <a href={`/fsignup`}>Sign Up</a>;
    }
    if (type === "user") {
      if (username == "") {
        social = "#12055c";
        login = (
          <a href={`/user/Home/`} style={{ textDecoration: "none" }}>
            Login
          </a>
        );
      } else {
        console.log("err");
      }
    } else {
      if (username == "") {
        social = "#d48b04";
        login = (
          <a href={`/vendor/Home`} style={{ textDecoration: "none" }}>
            Login
          </a>
        );
      }
    }

    return (
      <div className="container logintop ">
        <div className="">
          {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
          <div className=" " id="login-second">
            <div className="container">
              <div className="d-flex justify-content-center">
              
                <div className="card animated bounce" id="login-card">
                  <div className="card-header">
                    <h3 className="mt-5"> {type} signin </h3>
               
                  </div>
                  <div className="card-body">
                    
                    <form onSubmit={this.onSubmit}>
                      <div className="input-group form-group">
                        <div className="input-group-prepend">
                          <span
                            className="input-group-text"
                            style={{ background: social }}
                          >
                            <i className="fa fa-user"></i>
                          </span>
                        </div>
                        <input
                          type="text"
                          className="form-control"
                          placeholder="username"
                          value={this.state.username}
                          onChange={this.onChange.bind(this)}
                        />
                      </div>
                      <div className="input-group form-group">
                        <div className="input-group-prepend">
                          <span
                            className="input-group-text"
                            style={{ background: social }}
                          >
                            <i className="fa fa-key"></i>
                          </span>
                        </div>
                        <input
                          type="password"
                          className="form-control"
                          placeholder="password"
                          value={password}
                          onChange={this.onChange.bind(this)}
                        />
                      </div>

                      <div className="form-group ">
                        <button
                          type="submit"
                          value="Login"
                          className="btn float-right login_btn btn-block "
                          style={{
                            backgroundColor: social,
                          }}
                        >
                          {login}
                        </button>
                      </div>
                    </form>
                  </div>
                  <ul className="social-link">
                  <li >
                    <a href="(0)">
                      <i className="fa fa-twitter "></i>
                    </a>
                  </li>
                  <li className="facebook animated bounceIn wow delay-03s animated">
                    <a href="(0)">
                      <i className="fa fa-facebook "></i>
                    </a>
                  </li>
                  <li className="pinterest animated bounceIn wow delay-04s animated">
                    <a href="(0)">
                      <i className="fa fa-pinterest "></i>
                    </a>
                  </li>
                  <li className="dribbble animated bounceIn wow delay-06s animated">
                    <a href="(0)">
                      <i className="fa fa-instagram "></i>
                    </a>
                  </li>
               
                
                </ul>
                  <div className="card-footer">
                    <div className="d-flex justify-content-center links">
                      Don't have an account?
                      {signup}
                    </div>
                    <div className="d-flex justify-content-center">
                      <a href="/reset">Forgot your password?</a>
                      <a href="/main/Home">Admin</a>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Login;
