import React, { Component } from "react";
import "../CSS/donor.css";
import axios from"axios";

export default class addDonors extends Component {
  constructor(props) {
    super(props);

    this.state = {
      category: [],
      title: "",
      lastdate: "",
      eligibility: "",
      benefitsreward: "",
      category: "",
      website:"",
      file: null,
    };
    this.onChange = this.onChange.bind(this);
    this.handleDropdownChange = this.handleDropdownChange.bind(this);
  }
  componentDidMount() {
    this.props.getCategory();
  }
  // Input on change
  onChange(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
  }
  // Dropdown change
  handleDropdownChange(e) {
    this.setState({ category: e.target.value });
  }
  // fileupload
  onChangeHandler = (e) => {
    this.setState({
      file: e.target.files[0],
    });
  };
  onSubmit = async (e) => {
    e.preventDefault();
    // const data = new FormData();
    // data.append("file", this.state.file, this.state.file.name);

    // console.log(data);
    const token = sessionStorage.getItem("token");
    // const config = {
    //   headers: {
    //     Authorization: `Bearer ${token}`,
    //     "Content-Type": "multipart/form-data",
    //   },
    // };
    try {
      // const res = await axios.post(
      //   `http://localhost:5000/api/v1/vendors/products/photo`,
      //   data,
      //   config
      // );
      // console.log(res.data.data);

      const schemes = {
        title: this.state.title,
        lastdate: this.state.lastdate,
        category: this.state.category,
        eligibility: this.state.eligibility,
        benefitsreward: this.state.benefitsreward,
        website:this.state.website,
        // photo: res.data.data,
      };
      const body = JSON.stringify(schemes);
      console.log(body);
      const config1 = {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };
      const result = await axios.post(
        `http://localhost:5000/api/v1/gemploye/scheme`,
        body,
        config1
      );
      console.log(result.data.data);
      alert(`Scheme Added ${result.data.data.title}`);
    } catch (err) {
      // console.log("Can't load the items");
    }
  };

  render() {
    return (
      <div className="container itmtop">
        {console.log(this.state)}
        <div className="">
          {/* <div className="jumbotron col-md-6 col-sm-5 " id="login-first"></div> */}
          <div className="" id="login-second">
            <div class="page-wrapper p-t-50 p-b-50">
              <div class="wrapper wrapper--w900 ">
                <div class="card cardH card-6 ">
                  <div class="card-heading m-4">
                    <h2 class="title text-dark">Add Schemes</h2>
                  </div>
                  <div class="card-body">
                    <form method="POST"  onSubmit={this.onSubmit}>
                      <div class="form-row frow">
                        <div class="name">Scheme Title:</div>
                        <div class="value">
                          <input
                            class="input--style-6"
                            type="text"
                            name="title"
                            value={this.state.title}
                            onChange={this.onChange}
                          />
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Upload Images:</div>
                        <div class="value">
                          <div class="input-group js-input-file">
                            <input
                              class="input-file"
                              type="file"
                              name="file_doc"
                              id="file"
                              onChange={this.onChangeHandler}
                            />
                            <label class="label--file" for="file">
                              Choose file
                            </label>
                            <span class="input-file__info">No file chosen</span>
                          </div>
                          <div class="label--desc">
                            Upload your Document/Id proff or any other relevant
                            file. Max file size 50 MB
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Scheme Description</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="desc"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Select category</div>
                        <select
                          id="dropdown "
                          className="btn bg-success"
                          onChange={this.handleDropdownChange}
                        >
                          <option value="no cat">None</option>
                          {this.props.category.map((category) => (
                            <option key={category._id} value={category._id}>
                              {category.catname}
                            </option>
                          ))}
                          {/* <option value="N/A">N/A</option>
                          <option value="1">1</option>
                          <option value="2">2</option>
                          <option value="3">3</option>
                          <option value="4">4</option> */}
                        </select>
                      </div>
                      {/* <div class="form-row frow">
                        <div class="name">Upload Project Synopsis:</div>
                        <div class="value">
                          <div class="input-group js-input-file">
                            <input
                              class="input-file"
                              type="file"
                              name="file_doc"
                              id="file"
                            />
                            <label class="label--file" for="file">
                              Choose file
                            </label>
                            <span class="input-file__info">No file chosen</span>
                          </div>
                          <div class="label--desc">
                            Upload your Document/Id proff or any other relevant
                            file. Max file size 50 MB
                          </div>
                        </div>
                      </div> */}
                    
                  
                      <div class="form-row frow">
                        <div class="name">Benifits/Rewards</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="benefit"
                              value={this.state.benefitsreward}
                              onChange={this.onChange}
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Last Date</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="date"
                              name="date"
                              value={this.state.date}
                              onChange={this.onChange}
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Eligibility</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="eligibility"
                              value={this.state.eligibility}
                              onChange={this.onChange}
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      <div class="form-row frow">
                        <div class="name">Type</div>
                        <div class="value">
                          <select>
                            <option>School</option>
                            <option>Graduation</option>
                            <option>Post-Graduation</option>
                            <option>PHD</option>
                          </select>
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="type"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      {/* <div class="form-row frow">
                        <div class="name">Phone Number</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="email"
                              name="email"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div> */}
                      <div class="form-row frow">
                        <div class="name">Url Link</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="text"
                              name="url"
                              value={this.state.url}
                              onChange={this.onChange}
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div>
                      {/* <div class="form-row frow">
                        <div class="name">Email</div>
                        <div class="value">
                          <div class="input-group">
                            <input
                              class="input--style-6"
                              type="email"
                              name="email"
                              placeholder=""
                            />
                          </div>
                        </div>
                      </div> */}
                    </form>
                  </div>
                  <div class="card-footer">
                    <button class="btn btn--radius-2 btn-gray" type="submit">
                      ADD
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
